import React, {Component} from 'react';

const Army = (Men, shot) =>{
    class NewMen extends Component{
        state = {
            gunshots: 0
        }
    
        handleShots = () => {
            this.setState({gunshots: this.state.gunshots+ shot})
        }
        
        render(){
           
            return <Men 
            hoccamp={this.props.camp} 
            hocgunname = 'AK-47' 
            hocgunshots ={this.state.gunshots} 
            hochandleShots = {this.handleShots}
            {...this.props}
            />
        }
    }
    return NewMen;
}

export default Army;
