import React from "react";
import ReactDOM from "react-dom";
import cycle from './cycle.jpg';
import shop3 from './shop3.jpg';
import football from './football.jpg';
import './index.css'


let date = new Date(2020, 9, 8, 22);
let hour = date.getHours();

let greet = '';

const greetStyle= {}

if(hour>=1 && hour <12){
    greet = 'Good Morning';
    greetStyle.color= 'green';
}else if(hour >= 12 && hour< 16){
    greet = 'Good Afternoon';
    greetStyle.color= 'yellow';
}else if(hour >= 16 && hour< 19){
    greet = 'Good Evening';
    greetStyle.color= 'orange';
}else{
    greet = 'Good Night';
    greetStyle.color= 'b';
}



ReactDOM.render(
<>
<h1 style={greetStyle}>Hello Lathesh - {greet}</h1>
</>

,document.getElementById('root'));
