import React, { useState } from 'react';

function App(){
    // const nameStateVariable = useState('Lathesh');
    // const name = nameStateVariable[0];
    // const setName = nameStateVariable[1];
    const[name, setName] = useState('Lathesh');
   
   
    const change = () => {
        setName('Manju');
    }

    return (
        <React.Fragment>
            <h1>N: {name}</h1>
            <button onClick={change}>Click</button>
        </React.Fragment>
    )
}

export default App;
