import React, { Component } from 'react'

export default class Sonam extends Component {
    state = {
        gunshots: 0
    }

    handleOver = () => {
        this.setState({gunshots: this.state.gunshots + 1})
    }
    render() {
        return (
            <div>
                <h2 onMouseOver = {this.handleOver}>Sonam Gunshots: {this.state.gunshots}</h2>
            </div>
        )
    }
}
