import React, { useState } from 'react';

function App(){
    // const nameStateVariable = useState('Lathesh');
    // const name = nameStateVariable[0];
    // const setName = nameStateVariable[1];
    const[name, setName] = useState('Lathesh');
    const[roll, setRoll] = useState(101)
   
   
    const change = () => {
        setName('Manju');
        setRoll(4000)
    }

    return (
        <React.Fragment>
            <h1>Name: {name}</h1>
            <h2>Roll: {roll}</h2>
            <button onClick={change}>Click</button>
        </React.Fragment>
    )
}

export default App;
