import React, { Component } from 'react'
import Army from './withArm';

class Sonam extends Component {
    state = {
        gunshots: 0
    }

    handleShots = () => {
        this.setState({gunshots: this.state.gunshots+1})
    }
    render() {
        return (
            <div>
                 <h1>Camp: {this.props.camp}</h1>
                <h2 onMouseOver = {this.props.hochandleShots}>Sonam {this.props.hocgunname} Gunshots: {this.props.hocgunshots}</h2>
            </div>
        )
    }
}

export default Army(Sonam, 20);
