import React, { Component } from 'react'
import Sonam from './Sonam';
import Army from './withArm'

export default class Rahul extends Component {
    state = {
        gunshots: 0
    }

    handleOver = () => {
        this.setState({gunshots: this.state.gunshots+1})
    }
    render() {
        return (
            <div>
                <h2 onMouseOver = {this.handleOver}>Rahul {this.props.ho} Gunshots: {this.state.gunshots}</h2>
                <Sonam/>
            </div>
        )
    }
}
